css.cssText:
.e_bannerD .swiper-container{
    width: 100%;
    height: 100vh;
    max-height: 1080px;
    overflow: hidden;
}
.e_bannerD .swiper-slide{
    overflow: hidden;
    background: #fff;
    z-index: 10;
}
.e_bannerD .swiper-slide-active{
    z-index: 11;
}
.e_bannerD .p_img{
    width: 100%;
    height: 100%;
    overflow: hidden;
}
.e_bannerD .p_video{
    width: 100%;
    height: 100%;
    overflow: hidden;
}
.e_bannerD .P_link_img{
    display: block;
    position: relative;
    overflow: hidden;
    height: 100%;
    width: 100%;
    background-size: cover;
    transition: all ease 2s;
    transform: scale(1.1);
}
.e_bannerD  .swiper-slide-active .P_link_img{
    transform: scale(1);
}
.e_bannerD .p_videoPoster{
    display: block;
    position: relative;
    overflow: hidden;
    height: 100%;
    width: 100%;
    background-size: cover;
    transition: transform ease 2s;
    transform: scale(1.1);
    cursor: pointer;
    z-index: 21;
}
.e_bannerD  .swiper-slide-active .p_videoPoster{
    transform: scale(1);
}
.e_bannerD .P_link_img img{
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%,-50%)scale(1.18);
    width: auto;
    height: 100%;
}
.e_bannerD .p_videoPoster img{
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%,-50%)scale(1.18);
    width: auto;
    height: 100%;
}
.e_bannerD .p_info{
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    z-index: 22;
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding: 15px 20px;
    pointer-events: none;
}
.e_bannerD .p_info_fivebox{
    width: 40%;
}
.e_bannerD .p_summary{
    color: rgb(255 255 255 / 40%);
    font-family: arial;
    font-size: 20px;
    line-height: 1.6;
    margin-bottom: 30px;
    transition: all ease .8s .3s;
    opacity: 0;
    transform: translateY(40px);
}
.e_bannerD .swiper-slide-active .p_summary{
    opacity: 1;
    transform: translateY(0px);
}
.e_bannerD .p_btitle{
    color: #fff;
    font-size: 40px;
    line-height: 1.3;
    transition: all ease .8s .6s;
    opacity: 0;
    transform: translateY(40px);
}
.e_bannerD .swiper-slide-active .p_btitle{
    opacity: 1;
    transform: translateY(0px);
}
.e_bannerD .p_cspan{
    position: relative;
    margin-top: 30px;
    display: block;
    transition: all ease .8s .9s;
    opacity: 0;
    transform: translateY(40px);
    display: none;
}
.e_bannerD .p_button{
    display: inline-block;
    margin-top: 40px;
    border-radius: 30px;
    border: 1px solid #fff;
    width: 100%;
    max-width: 150px;
    padding: 10px 15px;
    color: #fff;
    text-align: center;
    font-size: 14px;
    opacity: 0;
    transform: translateY(40px);
    transition: all ease .3s,transform ease .8s .9s,opacity ease .8s .9s;
}
.e_bannerD .p_button:hover{
    border: 1px solid #fff;
    background:#fff;
    color: #333;
}
.e_bannerD .swiper-slide-active .p_button{
    opacity: 1;
    transform: translateY(0px);
}
.e_bannerD .swiper-slide-active .p_cspan{
    opacity: 1;
    transform: translateY(0px);
}
.e_bannerD .p_cspan::before,
.e_bannerD .p_cspan::after{
    content:'';
    width: 18px;
    height: 18px;
    border-radius: 100%;
    background: rgb(255 255 255 / 70%);
    display: inline-block;
}
.e_bannerD .p_cspan::before{
    background: rgb(255 255 255 / 70%);
}
.e_bannerD .p_cspan::after{
    margin-left: -6px;
}
.e_bannerD .p_scrool{
    position: absolute;
    left: 20px;
    bottom: 40px;
    color: #fff;
    z-index: 10;
}
.e_bannerD .p_scrool span {
    text-transform: uppercase;
    color: #fff;
    display: block;
    font-family: arial;
    writing-mode: tb;
    line-height: 20px;
    margin-bottom: 12px;
    font-size: 12px;
}
.e_bannerD .p_scrool i{
    display: block;
    width: 20px;
    height: 30px;
    border-radius: 12px;
    border: 2px #fff solid;
    position: relative;
}
.e_bannerD .p_scrool i:before {
    content: "";
    display: block;
    width: 4px;
    height: 10px;
    background: #fff;
    border-radius: 2px;
    position: absolute;
    left: 50%;
    transform: translate(-50%,0);
    top: 3px;
    animation: scrollmouse 2s linear infinite;
}
@keyframes scrollmouse{
    from {
        transform: translate(-50%,0);
        opacity:1;
    }to {
        transform: translate(-50%,10px);
        opacity:0;
    }
}
.e_bannerD .p_kongjian{
    position: absolute;
    right: 0;
    width: 100%;
    border-left: 1px solid rgb(255 255 255 / 10%);
    height: 100%;
    top: 0;
    bottom: 0;
    z-index: 1;
    max-width: 100px;
    pointer-events: none;
}
.e_bannerD .swiper-pagination{
    position: absolute;
    display: flex;
    flex-direction: column;
    top: 50%;
    right: 50px;
    transform: translate(50%,-50%);
    left: auto;
    width: auto;
    height: auto;
    bottom: auto;
}
.e_bannerD .swiper-pagination-bullet{
    margin: 3px 0 !important;
    background: hsl(0deg 0% 100% / 40%);
    opacity: 1;
    width: 10px;
    height: 10px;
    transition: all ease .3s;
}
.e_bannerD .swiper-pagination-bullet-active{
    height: 30px;
    border-radius: 15px;
    background: rgb(255 255 255);
}
.e_bannerD .swiper-pagination-bullet:hover{
    height: 15px;
    border-radius: 15px;
    background: rgb(255 255 255);
}
.e_bannerD .p_btn_next{
    color: #fff;
    border-radius: 100%;
    border: 2px solid #fff;
    width: 50px;
    height: 50px;
    transition: all ease .3s;
    position: static;
    margin: 0 auto;
}
.e_bannerD .p_btn_next::after{
    font-size: 16px;
}
.e_bannerD .p_btn_next:hover{
    background: #000;
    border: 2px solid #000;
}
.e_bannerD .p_btn_prev{
    color: #fff;
    border-radius: 100%;
    border: 2px solid #fff;
    height: 50px;
    width: 50px;
    transition: all ease .3s;
    position: static;
    margin: 10px auto 0;
}
.e_bannerD .p_btn_prev::after{
    font-size: 16px;
}
.e_bannerD .p_btn_prev:hover{
    background: #000;
    border: 2px solid #000;
}
.e_bannerD .videoContent{
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    bottom: 0;
    z-index: 20;
    cursor: pointer;
}
.e_bannerD .videoContent video{
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%,-50%);
    height: auto;
    width: 100%;
}
.e_bannerD .p_video_button{
    position: absolute;
    right: 0;
    width: 100%;
    bottom: 160px;
    z-index: 110;
    max-width: 100px;
    height: 50px;
}
.e_bannerD .p_video_button span{
    color: #fff;
    border-radius: 100%;
    border: 2px solid #fff;
    top: auto;
    bottom: 0;
    width: 50px;
    height: 50px;
    left: 50%;
    transform: translateX(-50%);
    transition: all ease .3s;
    display: block;
    position: absolute;
    cursor: pointer;
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
}
.e_bannerD .p_video_button svg{
    margin: 0 auto;
    width: 15px;
}
.e_bannerD .p_arr{
    max-width: 100px;
    width: 100%;
    position: absolute;
    right: 0;
    bottom: 40px;
    height: auto;
    z-index: 1;
    display: flex;
    flex-direction: column;
    justify-content: center;
    text-align: center;
}
.e_bannerD .p_video_button span:hover{
    background: #000;
    border: 2px solid #000;
}

.e_bannerD .p_info_fivebox {
    width: 75%;
    padding-bottom: 90px;
}
.e_bannerD .p_summary{
    font-size: 14px;
    line-height: 1.2;
    margin-bottom: 20px;
}
.e_bannerD .p_btitle{
    font-size: 22px;
}
.e_bannerD .p_cspan::before, .e_bannerD .p_cspan::after{
    width: 10px;
    height: 10px;
}
.e_bannerD .p_cspan::after{
    margin-left: -3px;
}
.e_bannerD .p_kongjian{
    max-width: 55px;
}
.e_bannerD .p_arr{
    max-width: 55px;
}
.e_bannerD .p_video_button{
    max-width: 55px;
    bottom: 106px;
    height: 28px;
}
.e_bannerD .swiper-pagination-bullet{
    width: 6px;
    height: 6px;
}
.e_bannerD .swiper-pagination-bullet-active{
    height: 20px;
}
.e_bannerD .p_btn_prev{
    width: 28px;
    height: 28px;
    margin: 5px auto 0;
}
.e_bannerD .p_video_button span{
    width: 28px;
    height: 28px;
}
.e_bannerD .p_btn_next{
    width: 28px;
    height: 28px;
}
.e_bannerD .p_btn_prev::after{
    font-size: 12px;
}
.e_bannerD .p_btn_next::after{
    font-size: 12px;
}
.e_bannerD .swiper-container{
    height: 70vh;
}
.e_bannerD .p_cspan{
    margin-top: 20px;
}
.e_bannerD .p_video_button svg{
    width: 12px;
}
.e_bannerD .swiper-pagination{
    right: 27px;
}
.e_bannerD .videoContent video{
    height: 100%;
    width: auto;
}
.e_bannerD .p_scrool span{
    display: none;
}
