css.cssText:
.e_button{
    display: block;
    position: relative;
    overflow: hidden;
    text-align: center;
    box-sizing: border-box;
    transition: all .3s;
    transform-style: preserve-3d;
    width: 100px;
    color: #fff;
    border-radius: 4px;
    background: rgba(64, 158, 244, 1);
    padding: 8px;
}
.e_button span {
	display: block;
	width: 100%;
	position: relative;
	z-index: 1;
	outline: 0;
	margin: 0px;
    overflow: hidden;
}
.e_button:hover {
	text-decoration: none;
    background-color: #66b1f6;
}
.e_button::after {
	content: "";
	position: absolute;
	width: 100%;
	height: 100%;
	transition: all .3s;
	z-index: 0;
	opacity: 0;
	top: 0;
    left: 0px;
}
.e_button:hover::after {
	opacity: 1
}
