css.cssText:
.e_button{
    display: block;
    position: relative;
    overflow: hidden;
    text-align: center;
    box-sizing: border-box;
    width: 100px;
    border-radius: 4px;
    padding: 8px;
}
.e_button span {
	display: block;
	width: 100%;
	position: relative;
	z-index: 1;
	outline: 0;
	margin: 0px;
    overflow: hidden;
}
.e_button::after {
	content: "";
	position: absolute;
	width: 0;
	height: 100%;
	transition: all .3s;
	z-index: 0;
	opacity: 0;
	top: 0;
    left: 0px;
}
.e_button:hover::after {
	opacity: 1
}
