css.cssText:
.e_categoryA{
    user-select:none;
    font-size:14px;
    width: 100%;
    max-width: 260px;
    background: #f5f5f5;
}
.e_categoryA ul{
    list-style: none;
    margin:0;
}
.e_categoryA li{
    position:relative;
}
.e_categoryA .p_c_title a{
    width: 100%;
    padding-left: 15px;
    display: flex;
    justify-content: flex-start;
}
.e_categoryA .deep-1-1{
    display: flex;
    justify-content: space-between;
    position:relative;
    line-height: 50px;
    border-bottom: 1px solid #ebebeb;
    font-size: 15px;
    transition: all ease .3s;
    width: 100%;
    color: #333;
}
.e_categoryA .p_arrow{
    position:absolute;
    right:10px;
    top: 13px;
    width:16px;
    height: 16px;
    transform:rotate(-90deg);
    fill: currentColor;
}
.e_categoryA .deep-2{
    background: #ededed;
    position: absolute;
    z-index: 9;
    width: 230px;
    left: 100%;
    top: 0;
}
.e_categoryA .p_arrow{
    margin: 0;
    top: 50%;
    font-size: 12px;
    transform: translateY(-50%)rotate(-180deg);
    line-height: 1;
    padding: 0;
    width: auto;
    height: 14px;
    right: 15px;
    pointer-events: none;
}
.e_categoryA .deep-2-2{
    display: flex;
    justify-content: space-between;
    position:relative;
    line-height: 50px;
    border-bottom: 1px solid #e1e1e1;
    font-size: 15px;
    transition: all ease .3s;
    width: 100%;
    color: #666;
}
.e_categoryA .deep-3{
    background: #e3e3e3;
    position: absolute;
    z-index: 9;
    width: 230px;
    left: 100%;
    top: 0;
}
.e_categoryA .deep-3-3{
    display: flex;
    justify-content: space-between;
    position:relative;
    line-height: 50px;
    border-bottom: 1px solid #d9d9d9;
    font-size: 15px;
    transition: all ease .3s;
    width: 100%;
    color: #666;
}

.e_categoryA .p_c_images{
    width: 27px;
    height: 100%;
    position: relative;
    display: flex;
    flex-direction: column;
    justify-content: center;
    margin-right: 6px;
    order: -1;
    overflow: hidden;
    transition: all ease .3s;
}
.e_categoryA .p_c_images img{
    width: 100%;
    height: auto;
    vertical-align:middle;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%,-50%);
}
.e_categoryA p:hover .p_c_images{
    opacity:0;
    margin-right: 0px;
    width: 0px;
}

.e_categoryA .p_c_title:hover{
    background: #007bff;
    color: #fff;
    border-bottom: 1px solid #007bff;
}
.e_categoryA .active > p{
    background: #007bff;
    color: #fff;
    border-bottom: 1px solid #007bff;
}

.e_categoryA li:last-child > .p_c_title{
    border-bottom: 0px solid #ebebeb;
}

.e_categoryA{
    background: none;
    max-width: none;
    display: flex;
    justify-content: flex-end;
}
.e_categoryA .p_c_tree{
    position:fixed;
    top:0;
    bottom: 0;
    left: 100%;
    height: 100vh;
    width: 70%;
    transition: all ease .5s;
    background: #fff;
    z-index: 999;
    max-height: 1080px;
}
.e_categoryA .deep-1{
    height:100%;
    overflow-y:auto;
    overflow-x:hidden;
}
.e_categoryA .p_c_mbtn{
    display: block;
    border: 0;
    background: #007bff;
    padding: 10px 15px;
    border-radius: 2px;
    font-size: 14px;
    color: rgb(255 255 255 / 80%);
    outline: none;
}
.e_categoryA .p_c_mput{
    display:block;
    line-height: 50px;
    background: rgb(0 0 0 / 70%);
    position: fixed;
    top: 0;
    bottom: 0;
    right: 0;
    left: 0;
    max-height: 1080px;
    height: 100vh;
    width: 100%;
    cursor: pointer;
    transition: all ease .5s;
    opacity: 0;
    visibility: hidden;
    z-index: 999;
}
.e_categoryA .p_c_mput:hover{
    background: rgb(0 0 0 / 80%);
}
.e_categoryA .p_c_title a{
    width: auto;
    padding-right: 15px;
}
.e_categoryA .p_c_title{
    color: #666;
}
.e_categoryA .deep-2{
    display: none;
    position: static;
    width: 100%;
}
.e_categoryA .deep-3{
    display: none;
    position: static;
    width: 100%;
    background: #dfdfdf;
}
.e_categoryA .deep-3-3{
    border-bottom: 1px solid #d5d5d5;
}
.e_categoryA.design_nomput .p_c_mput{
    display:none;
}

.p_chuShi{
    transition: all ease .5s;
}
.p_leftani{
    transform: translateX(-70%);
}
.p_leftani .e_categoryA .p_c_mput{
    opacity: 1;
    visibility: visible;
    display: block;
}
.p_leftaniAdmi{
    transform: translateX(-90%);
}
.p_leftaniAdmi .e_categoryA .p_c_tree{
    width: 90%;
}
