css.cssText:
.e_categoryB {
    user-select:none;
    font-size:14px;
}
.e_categoryB  ul{
    list-style-type:none;
    padding-left:30px;
    margin:0;
}
.e_categoryB  .p_c_tree>ul{
    padding:0;
}
.e_categoryB  li{
    position:relative;
}
.e_categoryB  li .p_c_title{
    display:block;
    position:relative;
    line-height:42px;
    border-bottom:1px solid #e4e7ec;
    cursor:pointer;
    padding-left:10px;
}
.e_categoryB  li .p_c_title.active,
.e_categoryB  li .p_c_title:hover{
    background:#e1f6ff;
}
.e_categoryB  li .p_c_title img{
    width:20px;
    height:20px;
    vertical-align:middle;
    margin-right:5px;
}
.e_categoryB  .p_c_title .p_arrow{
    position:absolute;
    right:10px;
    top: 13px;
    width:16px;
    height: 16px;
    transform:rotate(-90deg);
    color:#ccc;
    fill: currentColor;
}
.e_categoryB  .p_c_mbtn,
.e_categoryB  .p_c_mput,
.e_categoryB  .p_c_item>.p_c_content{
    display:none;
}

.e_categoryB .p_c_tree{
    position:fixed;
    top:0;
    right:-100%;
    height:100%;
    padding-left:36px;
    width:100%;
    transition:right 0.2s;
    background:#555;
    z-index: 99;
}
.e_categoryB .p_c_tree.show{
    right:0
}
.e_categoryB .p_c_tree>ul{
    height:100%;
    overflow-y:auto;
    overflow-x:hidden;
}
.e_categoryB .p_c_mbtn{
    display:inline-block;
}
.e_categoryB .p_c_tree .p_c_mput{
    display:block;
    text-align:center;
    width:36px;
    height:100%;
    line-height:100vh;
    background:#888;
    position:absolute;
    left:0px;
}
.e_categoryB .p_c_tree .p_c_mput .icon{
    transform: rotate(180deg);
    fill: #ccc;
}
.e_categoryB .p_c_title{
    color:#f4f4f5
}
.e_categoryB li .p_c_title.active,.e_categoryB li .p_c_title:hover{
    color:#555;
}
.e_categoryB li .p_c_title.active a,.e_categoryB li .p_c_title:hover a{
    color:#555;
}
