css.cssText:
.e_categoryC{
    user-select:none;
}
.e_categoryC .p_c_tree{
    background: #f5f5f5;
}
.e_categoryC .p_c_tree .p_c_content{
    display: flex;
    justify-content: flex-start;
}
.e_categoryC .p_c_item{
    position: relative;
}
.e_categoryC .p_c_item span{
    display: block;
}
.e_categoryC .p_c_title{
    font-size: 15px;
    width: 100%;
    padding-top: 0;
    padding-bottom: 0;
    padding-left: 15px;
    padding-right: 15px;
    line-height: 50px;
    color: rgb(0 0 0 / 70%);
    display: block;
    background: rgb(255 255 255 / 0%);
    transition: all ease .3s;
    position: relative;
    height: 100%;
    overflow: hidden;
    min-width: 120px;
    text-align: center;
}
.e_categoryC .p_c_title::after{
    content:'';
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 1px;
    height: 12px;
    background: rgb(0 0 0 / 15%);
}
.e_categoryC .p_c_item:last-child .p_c_title::after,
.e_categoryC .p_c_item > .p_c_content .p_c_title::after{
    display:none;
}
.e_categoryC .p_c_item > .p_c_content{
    position: absolute;
    left: 0;
    top: 100%;
    z-index: 9;
    background: #ededed;
    width: 100%;
    display: none;
}
.e_categoryC .p_c_item > .p_c_content .p_c_title,
.e_categoryC .p_c_item > .p_c_content > .p_c_item > .p_c_content .p_c_title{
    font-size: 14px;
    color: rgb(0 0 0 / 60%);
}
.e_categoryC .p_c_content .p_c_content .p_c_content{
    background: #e3e3e3;
    top: 0;
    left: 100%;
}
.e_categoryC .p_c_title:hover,
.e_categoryC .p_c_item > .p_c_content .p_c_title:hover,
.e_categoryC .active .p_c_title,
.e_categoryC .p_c_item > .p_c_content .active .p_c_title,
.e_categoryC .p_c_item > .p_c_content > .p_c_item > .p_c_content .active .p_c_title{
    color: rgb(0 123 255);
}
