css.cssText:
.e_categoryC{
    user-select:none;
}
.e_categoryC .p_c_tree{
    background: #f5f5f5;
}
.e_categoryC .deep-1{
    max-width: 1400px;
    width: 100%;
    margin: 0 auto;
    display: flex;
    justify-content: flex-start;
}
.e_categoryC .p_c_item{
    position: relative;
}
.e_categoryC .deep{
    font-size: 15px;
    width: 100%;
    padding-top: 0;
    padding-bottom: 0;
    padding-left: 15px;
    padding-right: 15px;
    line-height: 50px;
    color: rgb(0 0 0 / 70%);
    display: block;
    background: rgb(255 255 255 / 0%);
    transition: all ease .3s;
    position: relative;
    height: 100%;
    overflow: hidden;
    min-width: 120px;
    text-align: center;
}
.e_categoryC .deep-2{
    position: absolute;
    left: 0;
    top: 100%;
    z-index: 9;
    background: #ededed;
    width: 100%;
    display: none;
}
.e_categoryC .p_c_title2{
    font-size: 14px;
    color: rgb(0 0 0 / 60%);
}
.e_categoryC .p_c_title3{
    font-size: 14px;
    color: rgb(0 0 0 / 60%);
}
.e_categoryC .deep-3{
    display: none;
    background: #e3e3e3;
    top: 0;
    left: 100%;
    position: absolute;
}
.e_categoryC .deep:hover,
.e_categoryC .active > p > .deep{
    color: rgb(0 123 255);
}
.e_categoryC .p_c_menu{
    display: none;
}

.e_categoryC .deep-1{
    flex-direction: column;
    padding: 0 15px;
}
.e_categoryC .deep{
    text-align: left;
    padding: 0;
}
.e_categoryC .deep-2{
    position: static;
    padding: 0 15px;
}
.e_categoryC .deep-3{
    position: static;
    background: none;
}
.e_categoryC .p_c_t{
    display: flex;
    justify-content: space-between;
    border-bottom: 1px solid rgb(0 0 0 / 5%);
}
.e_categoryC .p_c_item:last-child > p{
    border-bottom: 0px solid rgb(0 0 0 / 10%);
}
.e_categoryC .p_c_menu{
    display: flex;
    flex-direction: column;
    justify-content: center;
    padding-left: 15px;
    cursor: pointer;
}
.e_categoryC .p_c_menu svg:last-child{
    display:none;
}
.e_categoryC svg{
    margin: 0;
    width: 16px;
}
.e_categoryC .show > p > .p_c_menu svg:first-child{
    display:none;
}
.e_categoryC .show > p > .p_c_menu svg:last-child{
    display:block;
}

.e_categoryC .deep::after{
    content:'';
    position: absolute;
    right: 0;
    top: 50%;
    transform: translateY(-50%);
    width: 1px;
    height: 12px;
    background: rgb(0 0 0 / 15%);
}
.e_categoryC .p_c_item:last-child .deep::after,
.e_categoryC .p_c_item > .p_c_content .deep::after{
    display:none;
}
