comps.template:
{{#with c_text_list_001-21623291951570}}
<div class="e_comp comp_contaner">
    {{#if data.category.summary}}
    <div class="border p-3 p_description" data-ename="分类摘要">
        <h6 class="e_subtitle p_description_title" data-ename="摘要标题">{{i18n.desTitle}}</h6>
        <p class="text-secondary e_subtitle p_description_con" data-ename="摘要内容">{{data.category.summary}}</p>
    </div>
    {{/if}}
    <div class="e_list p_data_list clearfix" data-ename="循环体容器">
        {{#data.list}}
        <div class="e_item p_data_item" data-ename="循环体外框">
            <div class="border p_data_item_item" data-ename="循环体元素">
                <span class="p_item_img" data-ename="图片容器">
                    <a bind="href-href" href="{{href}}">
                        {{{lazyImage coverImage 'class="p_img" data-ename="图片" title="{{title}}" alt="{{title}}" bind="src-coverImage|title-title|alt-title"' 'true'}}}
                    </a>
                </span>
                <div class="p_item_info" data-ename="数据信息容器">
                    <p>
                        <span class="text-secondary p_item_time" data-ename="更新时间" bind="data-updateDate">{{#dateFormat updateDate}}{{/dateFormat}}</span>
                    </p>
                    <h6 class="e_subtitle p_title" data-ename="数据标题">
                        <a bind="href-href" href="{{href}}" class="text-body e_link p_item_title" data-ename="数据标题链接">
                            <span bind="data-title">{{title}}</span>
                        </a>
                    </h6>
                    <p class="e_summary p_item_description" data-ename="数据描述" bind="data-summary">{{summary}}</p>
                </div>
            </div>
        </div>
        {{^}}
        {{{noDataPrompt}}}
        {{/data.list}}
    </div>
    {{#if data.list}}
    <div class="p_pagenation text-center" data-ename="分页容器">
        {{{paging data.page prop.pageType ../view ../pagingData}}}
    </div>
    {{/if}}
</div>
<script>
    $comp({
        id: 'c_text_list_001-21623291951570',
        lib: ['c_text_list_001_2.js'],
        pageParams:{
            url:"{{{dataApi}}}",
            page:{
                "pageSize":"{{data.page.pageSize}}",
                "currentPage":"{{data.page.currentPage}}",
                "totalPage":"{{data.page.totalPage}}",
                "totalCount":"{{data.page.totalCount}}"
            }
        },
        propJson: {{{propJson}}},
        i18nJson: {{{i18nJson}}}
    })
</script>
{{/with}}

