css.cssText:
.e_floatingShoppingCart .p_mini_cart{
  margin: 0 auto;
  text-align: center;
  position: relative;
  z-index: 20;
}
.e_floatingShoppingCart i{
  font-style: normal;
}
.e_floatingShoppingCart .p_cardnum{
  min-width: 14px;
  border-radius: 1rem;
  font-weight: 100;
}
.p_text-white {
  margin-right: -7px;
  margin-left: -7px;
  width: 30px;
  height: 25px;
}
.e_floatingShoppingCart .p_content{
  position: absolute;
  top: 100%;
  width: 400px;
  text-align: left;
  right: calc(50% - 200px);
  z-index: 20;
}
.e_floatingShoppingCart .p_closeCart {
  display:none;
}
.e_floatingShoppingCart .p_arrow{
  position: absolute;
  top: -6px;
  left: 50%;
  filter: drop-shadow(0 2px 12px rgba(0,0,0,.03));
  border-color: transparent;
  border-style: solid;
  border-width: 6px;
  border-top-width: 0;
  border-bottom-color: #ebeef5;
}
.e_floatingShoppingCart .p_arrow:after{
  content: " ";
  position: absolute;
  top: 1px;
  margin-left: -6px;
  border-width: 6px;
  border-color: transparent;
  border-style: solid;
  border-top-width: 0;
  border-bottom-color: #fff;
}
.e_floatingShoppingCart .p_show_s{
  display: none;
}

.e_floatingShoppingCart .p_list{
  min-height: 100px;
}

.e_floatingShoppingCart .p_goods_list{
  max-height: 300px;
  overflow-y: auto;
  padding-right: 10px;
}
.e_floatingShoppingCart .p_list_item{
  padding: .5rem 0;
  border: none;
  border-bottom: 1px solid #dee2e6;
}
.e_floatingShoppingCart .p_title{
  line-height: 100%;
  padding-bottom: .7rem;
}
.e_floatingShoppingCart .p_price{
  text-align: right;
  width: calc(30% - 10px);
}
.e_floatingShoppingCart .p_btn_delete{
  cursor: pointer;
}
.e_floatingShoppingCart .p_commodity{
  width: calc(70% - 10px);
}
.e_floatingShoppingCart .p_goodsImg{
  float: left;
  width: 67px;
  height: 67px;
  margin-right:10px;
}
.e_floatingShoppingCart .p_commodity_img{
  width: 100%;
  height: 100%;
  object-fit: cover;
}
.e_floatingShoppingCart .p_commodity_cont{
  width:calc(70% - 10px);
  float: left;
}
.e_floatingShoppingCart .p_commodity_name{
  overflow: hidden;
  text-overflow: ellipsis;
  -webkit-line-clamp: 3;
  display: -webkit-box;
  -webkit-box-orient: vertical;
  min-height: 46px;
}
.e_floatingShoppingCart .p_commodity_specs{
  overflow: hidden;
  text-overflow: ellipsis;
  -webkit-line-clamp: 1;
  display: -webkit-box;
  -webkit-box-orient: vertical;
}
.e_floatingShoppingCart .p_webstat{
  display: flex;
  justify-content: space-between;
  align-items: center;
}
