}
.e_floatingShoppingCart .p_webstat_r{
  width: 30%;
  margin-left: 5px;
} */
.pl_confirm{
  position: fixed;
  left: 0;
  top: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  background:rgba(0, 0, 0, 0.5);
  text-align: center;
  z-index: 9999;
}
.pl_confirm_con{
  margin-top: 100px;
  display: inline-block;
  width: 420px;
  max-width: 90%;
  background:#fff;
  border-radius: 5px;
  padding:20px;
  text-align: left;
  position: relative;
}
.pl_confirm_con .pl_confirm_close{
  cursor: pointer;
  position:absolute;
  right: 20px;
  top:20px;
  line-height:1;
  color:#bbb;
}
.pl_confirm_con .pl_confirm_title{
  color:#333;
  font-weight: bold;
  padding-right:10px;
}
.pl_confirm_con .pl_confirm_content{
  line-height: 1.5;
  margin-bottom: 20px;
}
.pl_confirm_con .pl_confirm_oprate{
  text-align: right;
  padding-top: 10px;
}
.pl_confirm_con .pl_confirm_oprate .btn + .btn{
  margin-left:10px;
}

.p_goods_list::-webkit-scrollbar {
  width: 6px;
  height: 10px;
  background: none;
}

.p_goods_list::-webkit-scrollbar-track {
  background: none;
}

.p_goods_list::-webkit-scrollbar-thumb {
  background-color: #E0E0E0;
  border-radius: 3px;
}

.p_goods_list::-webkit-scrollbar-button {
  height: 0;
  background: none;
}

.e_floatingShoppingCart .p_mini_cart{
  text-align: auto;
  width: 40px;
  height: 40px;
  display: block;
  position: fixed;
  right: 20px;
  top: 40%;
  z-index: 10;
}
.e_floatingShoppingCart .p_price{
  text-align: right;
  width: calc(20% - 0px);
}
.e_floatingShoppingCart .p_commodity{
  width: calc(80% - 0px);
}
.e_floatingShoppingCart .p_closeCart {
  display:block;
}
.e_floatingShoppingCart .p_show_l{
  display: none;
}
.e_floatingShoppingCart .p_show_s{
  display: block;
  background:none;
  border:none;
  outline: none;
}
.e_floatingShoppingCart .p_show_s .icon{
  width: 40px;
  height: 40px;
}
.e_floatingShoppingCart .p_show_s .p_cardnum{
position: absolute;
left: 25px;
top: 0;
z-index: 9;
}
.e_floatingShoppingCart .p_content{
  top: 10%;
  display: none;
  width: 90%;
  position: fixed;
  z-index: 10;
  left: 5%;
  top: calc(31%);
  margin: 0;
  right: 5%;
}
.e_floatingShoppingCart .p_content.show_s{
  display: block;
}
.e_floatingShoppingCart .p_arrow{
  display: none;
}
