css.cssText:
.e_login {
}

/* 顶部 */

/* 登录方式高亮 */
.e_login .p_loginway {
    cursor: pointer;
    color: #666;
}
.e_login .p_loginway.active {
    color: #333;
}

/* body */
.e_login .p_p_tip {
    height: 18px;
    color: #fa7679;
    line-height: 18px;
    font-size: 12px;
    visibility: hidden;
}
/* 错误提示 */
.e_login .error_li p {
    visibility: visible;
}
.e_login .error_li input {
    border: 1px solid #fa7679;
}
.e_login .p_lable_text {
    display: flex;
    justify-content: space-between;
    color: #007bff;
}
.e_login .p_lable_text span {
    cursor: pointer;
}
.e_login .p_login_btn {
    width: 100%;
    margin-top: 20px;
    margin-bottom: 20px;
}
.e_login .p_error {
    background-color: #fff2f0;
    border: solid #ffccc8 1px;
    border-radius: 4px;
    color: #fa7679;
    font-size: 12px;
    margin-bottom: 8px;
    text-indent: 12px;
    display: none;
}
.e_login .p_input_div {
    position: relative;
}
.e_login .p_input_div div {
    position: absolute;
    right: 0;
    top: 0px;
    width: 100px;
    height: 100%;
    line-height: 34px;
    text-align: right;
    padding-right: 12px;
}
.e_login .p_select {
    position: relative;
}
.e_login .p_select select {
    position: absolute;
    left: 1px;
    top: 6px;
    width: 60px;
    color: #666;
    border: 0;
}
.e_login .p_indent {
    text-indent: 60px;
}
.e_login .p_pe_box {
    display: none;
}

.e_login .p_login_box {
    display: none;
}
.e_login .p_time-2 {
    display: none;
}
.e_login .p_popup_box {
    position: fixed;
    left: 0px;
    top: 0px;
    background: rgba(0, 0, 0, 0.4);
    width: 100%;
    height: 100%;
    filter: alpha(opacity=60);
    opacity: 0.6;
    display: none;
    z-index: 999;
}
.e_login .p_popup_password {
    position: absolute;
    width: 340px;
    height: 280px;
    top: 50%;
    left: 50%;
    background-color: #fff;
    display: none;
    cursor: pointer;
    z-index: 9999;
    margin-left: -170px;
    margin-top: -140px;
    border-radius: 5px;
}
.e_login .p_popup_password p {
    text-align: center;
    color: #333;
    font-size: 16px;
    padding: 20px 0px;
}
.e_login .p_popup_password div {
    text-align: center;
    color: #666;
    font-size: 14px;
    margin-bottom: 12px;
}
.e_login .p_popup_password li {
    width: 196px;
    height: 38px;
    border: solid #007bff 1px;
    background: #ecf5fe;
    text-align: center;
    line-height: 38px;
    color: #007bff;
    border-radius: 5px;
    margin-left: auto;
    margin-right: auto;
    margin-top: 12px;
}

.e_login .form-control-p {
    display: block;
    width: 100%;
    height: calc(1.5em + 0.75rem + 2px);
    padding: 0.375rem 0.75rem;
    font-weight: 400;
    line-height: 1.5;
    color: #495057;
    background-color: #fff;
    background-clip: padding-box;
    border: none;
    border-bottom: 1px solid #ced4da;
    border-radius: 0;
    transition: border-color 0.15s ease-in-out, box-shadow 0.15s ease-in-out;
}
.e_login input {
    outline: none;
}
.e_login .btn {
    border-radius: 1.25rem;
}

.e_login .error_li input {
    border: none;
    border-bottom: 1px solid #fa7679;
}

