}
.e_loop .p_split{
	margin: 0 5px
}
.e_loop .p_normal{
    margin-right: 16px;
}


.e_loop .p_fbutton{
	background-color: #007bff;
	border: 1px solid #409EFF;
	text-decoration: none;
	color: white;
	font-size: 14px;
	text-align: center;
	padding: 10px 25px;
	position: relative;
	overflow: hidden;

}
.e_loop .input-check,
.e_loop .input-radio{
	margin: 0 5px;
	vertical-align: text-top;
}
.e_loop .position-left{
	display:flex;

}
.e_loop .position-right{
	display: flex;
	flex-direction: row-reverse;
}
.e_loop .position-top{
	/* display: flex; */
}
.e_loop .position-bottom{
	display: flex;
	flex-direction: column-reverse;
}

.e_loop .p_mbutton{
	border: 1px solid #409EFF;
	text-decoration: none;
	color: #409EFF;
	font-size: 14px;
	text-align: center;
	padding: 10px 25px;
	position: relative;
	overflow: hidden;
}
.e_loop .p_mbutton:after{
	content: "";
display: none;
width: 16px;
height: 16px;
background-color: #409eff;
-webkit-transform: skewY(
-45deg);
transform: skewY(
360deg);
position: absolute;
bottom: 0px;
right: 0;
z-index: 1;
}
.e_loop .p_mbutton::before{
	content: "";
display: none;
width: 6px;
height: 9px;
border-right: #ffffff solid 2px;
border-bottom: #ffffff solid 2px;
-webkit-transform: rotate(
35deg);
transform: rotate(
35deg);
position: absolute;
bottom: 5px;
right: 4px;
z-index: 2;
}
.e_loop .p_active .p_mbutton::before{
	display: block;
}
.e_loop .p_active .p_mbutton::after{
	display: block;
}
