.swiper-container {
    width: 100%;
    height: 100%;
  }
  .swiper-slide {
    text-align: center;
    font-size: 18px;
    background: #fff;

    /* Center slide text vertically */
    display: -webkit-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    transition: 300ms;
    transform: scale(0.8);
  }
  .swiper-slide-active,.swiper-slide-duplicate-active{
    transform: scale(1);
  }
.p_template{
}
.swiper-button-prev,.swiper-button-next{
    background: #007bff;
    border-radius: 100%;
    width: 44px;
    height: 44px;
    color: #fff;
}
.swiper-button-next:after, .swiper-button-prev:after{
    font-size: 22px;
}
.p_template img{
    width: 100%;
}
