css.cssText:
.e_loop_A{

}
.e_loop_A .swiper-container {
    width: 100%;
    height: 100%;
  }
.e_loop_A .swiper-slide {
    text-align: center;
    font-size: 18px;

    /* Center slide text vertically */
    display: -webkit-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
    transition: 300ms;
    transform: scale(0.8);
  }
.e_loop_A .swiper-slide-active,.e_loop_A .swiper-slide-duplicate-active{
    transform: scale(1);
  }
.e_loop_A .p_template{
    width: 100%;
}
.e_loop_A .swiper-button-prev,.e_loop_A .swiper-button-next{
    background: #007bff;
    border-radius: 100%;
    width: 44px;
    height: 44px;
    color: #fff;
}
.e_loop_A .swiper-button-next:after,.e_loop_A .swiper-button-prev:after{
    font-size: 22px;
}
.e_loop_A .p_template img{
    width: 100%;
}
