css.cssText:
.e_loop_B{

}
.e_loop_B .swiper-container {
    width: 100%;
    height: 100%;
  }

.e_loop_B .swiper-slide {
    text-align: center;
    font-size: 18px;
    width: 50%;

    /* Center slide text vertically */
    display: -webkit-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
  }

  .e_loop_B .swiper-slide:nth-child(2n) {
    width: 50%;
  }

  .e_loop_B .swiper-slide:nth-child(3n) {
    width: 50%;
  }
  .e_loop_B .swiper-button-prev,.e_loop_B .swiper-button-next{
    background-color:#007bff;
    bottom: 10px;
    width: 56px;
    top: auto;

}
.e_loop_B .swiper-button-prev{
    left: auto;
    right: 64px;
    border-top-left-radius:50%;
    border-bottom-left-radius:50%;
    border-top-right-radius:0;
    border-bottom-right-radius:0;
    
}
.e_loop_B .swiper-button-next{
    border-top-right-radius:50%;
    border-bottom-right-radius:50%; 
    border-top-left-radius:0;
    border-bottom-left-radius:0;   
}
.e_loop_B .swiper-button-next:after, .e_loop_B .swiper-button-prev:after{
    font-size: 22px;
    color: #fff;
}
.e_loop_B .p_template{
    width: 100%;
}
.e_loop_B .swiper-slide-active, .e_loop_B .swiper-slide-duplicate-active{
  transform: scale();
}
