css.cssText:
.e_loop_C{
    overflow: hidden;
}
.e_loop_C .swiper-container {
    width: 100%;
    height: 100%;
  }

.e_loop_C .swiper-slide {
    text-align: center;
    font-size: 18px;
    width: 50%;

    /* Center slide text vertically */
    display: -webkit-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
  }

  .e_loop_C .swiper-slide:nth-child(2n) {
    width: 50%;
  }

  .e_loop_C .swiper-slide:nth-child(3n) {
    width: 50%;
  }
.e_loop_C .p_template{
    width: 100%;
}
.e_loop_C .swiper-pagination-bullet{
    width: 40px;
    height: 6px;
    border-radius: 14px;

}
.e_loop_C .swiper-slide-active, .e_loop_C .swiper-slide-duplicate-active{
  transform: scale();
}
