css.cssText:
.e_loop_E{

}
.e_loop_E .swiper-container {
    width: 100%;
    height: 100%;
  }

 .e_loop_E .swiper-slide {
    overflow:hidden;
  }
  
  .e_loop_E .slide-inner {
    width: 100%;
    height: 100%;
    left: 0;
    top: 0;
    background-size: cover;
    background-position: center;
    display: -webkit-box;
    display: -ms-flexbox;
    display: -webkit-flex;
    display: flex;
    -webkit-box-pack: center;
    -ms-flex-pack: center;
    -webkit-justify-content: center;
    justify-content: center;
    -webkit-box-align: center;
    -ms-flex-align: center;
    -webkit-align-items: center;
    align-items: center;
  }

.e_loop_E .swiper-button-prev,.e_loop_E .swiper-button-next{
    background-color:#007bff;
    width: 100px;
    height: 100px;
    border-radius: 100%;
    top: 30%;

}
.e_loop_E .swiper-button-next{
    right: auto;
    left: 220px;    
}
.e_loop_E .swiper-button-prev{
    right: auto;
    left: 100px;    
}
.e_loop_E .swiper-button-next:after,.e_loop_E .swiper-button-prev:after{
    font-size: 30px;
    color: #fff;
}
.e_loop_E .p_template{
    width: 100%;
}
.e_loop_E .swiper-slide-active, .e_loop_E .swiper-slide-duplicate-active{
    transform: scale();
  }
