css.cssText:
.e_loop_L {
}
/* 焦点放大循环体 开始 */
.e_loop_L .swiper-container.effect1 {
    width: 100%;
    height: 100%;
}
.e_loop_L .effect1 .swiper-slide {
    transition: all 0.3s ease;
    transform: scale(0.8);
}
.e_loop_L .effect1 .swiper-slide-active,
.e_loop_L .effect1 .swiper-slide-duplicate-active {
    transform: scale(1);
}
.e_loop_L .effect1 .swiper-button-prev,
.e_loop_L .effect1 .swiper-button-next {
    width: 44px;
    height: 44px;
    border-radius: 100%;
    color: #fff;
    background: #007bff;
}
.e_loop_L .effect1 .swiper-button-next:after,
.e_loop_L .effect1 .swiper-button-prev:after {
    font-size: 22px;
}
/* 焦点放大循环体 结束 */
