css.cssText:
.e_navigationA .p_level1Box{
    display: flex;
    justify-content: flex-end;
}
.e_navigationA .p_iconBox{
    height: 45px;
    width: 45px;
    border-radius: 4px;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.e_navigationA .p_openIcon{
    width: 100%;
    height: 36px;
}
.e_navigationA .p_closeIcon{
    width: 100%;
    height: 30px;
}
.e_navigationA .p_navButton {
    display: none;
}
.e_navigationA .p_level2Box{
    display: none;
    height: auto;
    padding: 15px 0;
    background: #fff;
    width: 160px;
    position: absolute;
    left: 50%;
    top: 100%;
    z-index: 99;
    transform: translateX(-50%);
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}
.e_navigationA .p_level3Box{
    display: none;
    height: auto;
    padding: 15px 0;
    background: #fff;
    background: #f5f5f5;
    width: 130px;
    position: absolute;
    top: 0;
    left: 100%;
    z-index: 9;
    border-radius: 5px;
    border-top-left-radius: 0px;
}
.e_navigationA .p_level1Item{
    position: relative;
    border: none;
}
.e_navigationA .p_level2Item{
    position: relative;
    width: 100%;
}
.e_navigationA .p_level3Item{
    width: 100%;
}
.e_navigationA .p_level1Item a{
    font-size: 16px;
    padding: 0 20px;
    line-height: 80px;
    display: block;
    text-align: center;
}
.e_navigationA .p_level2Item a{
    font-size: 15px;
    padding: 0 20px;
    line-height: 35px;
    display: block;
    text-align: center;
    opacity: .6;
    transition: all ease .3s;
}
.e_navigationA .p_level2Item a:hover{
    opacity: 1;
}

.e_navigationA .p_level1Box{
    display: block;
}
.e_navigationA .p_iconBox{
    display: block;
}
.e_navigationA .p_navContent{
    display: none;
    padding: 20px;
    height: 100%;
    width: 100%;
    position: fixed;
    top: 0;
    left: 0;
    background: #fbfbfb;
    z-index: 99;
}
.e_navigationA .p_navButton{
    display: flex;
    justify-content: flex-end;
}
.e_navigationA .p_level1Box{
    width: 100%;
}
.e_navigationA .p_level2Box, 
.e_navigationA .p_level3Box{
    padding: 0 0 0 30px;
    background: none;
}
.e_navigationA .p_jtIcon{
    float: right;
    transition: 0.5s;
    height: 30px;
    margin-top: 10px;
}
.e_navigationA .p_jtHover {
    transition: 0.5s;
    transform: rotate(90deg);
}
.e_navigationA .p_level2Box,.e_navigationA .p_level3Box{
    width: 100%;
    left: 0;
    transform: translateX(0);
    position: inherit;
    border-radius: 0;
}
.e_navigationA .p_level1Item{
    border-bottom: 1px solid rgb(0 0 0 / 5%);
    line-height: 50px;
    font-size: 14px;
}
.e_navigationA .p_level1Item a,.e_navigationA .p_level2Item a{
    line-height: 50px;
    font-size: 14px;
    padding: 0;
    display: inline;
    text-align: left;
    opacity: 1;
}

.e_navigationA .p_jtIcon{
    transform: rotate(90deg);
    display: none;
}
.e_navigationA .p_level2Item .p_jtIcon{
    display: none;
}
.e_navigationA .p_iconBox{
    display: none;
}
