css.cssText:
.e_navigationC .p_navItem1{
    line-height: 1; 
}
.e_navigationC .p_iconBox{
    height: 45px;
    width: 45px;
    background: rgba(0,0,0,.1);
    border-radius: 4px;
    margin: 0px 15px 0 0;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.e_navigationC .p_openIcon, 
.e_navigationC .p_closeIcon{
    width: 100%;
    height: 23px;
}
.e_navigationC .p_navButton{
    display: none;
}

.e_navigationC .p_navButton{
    display: flex; 
    justify-content: flex-end;
    padding: 18px 0;
}
.e_navigationC .p_navBox1{
    display: none;
    height: calc(100vh - 80px);
    position: fixed;
    top: 80px;
    left: 0;
    right: 0;
    background: #fbfbfb;
    padding: 15px;
    z-index: 99;
}
.e_navigationC .p_navBox2{ 
   display: none;
   padding-bottom: 20px;
}
.e_navigationC .p_navItem2 a{
    color: rgb(0 0 0 / 40%);
    line-height: 2;
    transition: all ease .3s;
}
.e_navigationC .p_navItem2 a:hover{
    color: #000;
}
.e_navigationC .p_navItem1{ 
    padding: 0;
    border-bottom: 1px solid rgba(0,0,0,.5);
 }
 .e_navigationC .p_navCon {
    display: flex;
    justify-content: space-between;
 }
 .e_navigationC .p_navCon a{
    line-height: 60px;
    font-size: 15px;
 }
.e_navigationC .p_navItem2{ 
    border: none;
}
.e_navigationC .p_img{ 
    display: none;
}
.e_navigationC .p_jtIcon{ 
    float: right;
    transition: 0.5s;
    margin: 20px 0;
    width: 40px;
}
.e_navigationC .p_jtHover {
    transition: 0.5s;
    transform: rotate(90deg);
}
.e_navigationC .p_closeIcon{ 
    display: none;
    float: right;
}
.e_navigationC .p_closeIcon, .e_navigationC .p_openIcon{ 
    cursor: pointer;
}
.e_navigationC .p_imgBox{
    display: none;
}

.e_navigationC .p_navBox1{
    position: relative;
    justify-content: flex-end;
}
.e_navigationC .p_navBox1, .e_navigationC .p_navBox2{
    width: 100%;
    display: flex;
}
.e_navigationC .p_navBox2{
    position: absolute; 
    top: 100%;
    left: 0;
    padding: 30px 40px;
    z-index: 99;
    background: #f5f5f5;
}
.e_navigationC .p_navBox2 ul{
    width: 60%;
    display: flex;
    flex-wrap: wrap;
    align-content: flex-start;
    position: relative;
}
.e_navigationC .p_imgBox{
    width: 30%;
    position: relative;
    height: 150px;
    background: rgba(0,0,0,.2);
    overflow: hidden;
}
.e_navigationC .p_imgBox img{
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.e_navigationC .p_navItem1{ 
    border: none;
}
.e_navigationC .p_navCon{
    position: relative;
}
.e_navigationC .p_navCon a{
    display: block;
    padding: 0 20px;
}
.e_navigationC .p_navCon::before{
    content: " ";
    height: 3px;
    width: 0;
    background-color: #409eff;
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    transition: width 0.5s ease;
    margin: 0 auto;
}
.e_navigationC .p_navItem1:hover .p_navCon::before{
    width: 100%;
}
.e_navigationC .p_navItem2{
    line-height: 1;
    width: auto;
    margin: 0 30px 0 0;
}
.e_navigationC .p_navItem2:hover{ 

}
.e_navigationC .p_navItem2 span{
    line-height: 1;
    transition: all ease .3s;
    opacity: .6;
}
.e_navigationC .p_navItem2:hover span{
    opacity: 1;
}
.e_navigationC p{
    display: flex;
    flex-direction: column;
    align-items: center;
}
.e_navigationC span{
    text-align: center;
    font-size: 16px;
    line-height: 100px;
    display: block;
}
.e_navigationC .p_img{
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%,-50%);
    height: 100%;
    width: auto;
}
.e_navigationC .p_jtIcon{
    display: none;
}
.e_navigationC .p_iconBox{
    display: none;
}
.e_navigationC .p_navBox2 > div{
    width: 100%;
    max-width: 900px;
    margin: 0 auto;
    display: flex;
    justify-content: space-between;
}
.e_navigationC .p_navBox2 ul::after{
    content: '';
    border-left: 1px solid rgba(0, 0, 0, .1);
    position: absolute;
    top: -2px;
    bottom: -2px;
    left: -45px;
    z-index: 2;
    width: 1px;
}
