css.cssText:
.e_navigationE{
    display: flex;
    justify-content: flex-end;
}
.e_navigationE .p_navButton{
    width: 100px;
    display: flex;
    justify-content: center;
    padding: 20px
 0;
}
.e_navigationE .p_iconBox{
    width: 21px;
    height: 16px;
    cursor: pointer;
}

.e_navigationE .p_iconBox span {
    width: 100%;
    background: #333;
    display: block;
    height: 2px;
    margin: 2px 0;
    float: left;
    -webkit-transition: all ease 0.8s,opacity ease 0.3s;
    -moz-transition: all ease 0.8s,opacity ease 0.3s;
    -ms-transition: all ease 0.8s,opacity ease 0.3s;
    -o-transition: all ease 0.8s,opacity ease 0.3s;
    transition: all ease 0.8s,opacity ease 0.3s;
    opacity: 1;
}

.e_navigationE .p_iconBox span:nth-of-type(1) {
    width: 50%;
    float: left;
}

.e_navigationE .p_iconBox span:nth-of-type(2) {
}

.e_navigationE .p_iconBox span:nth-of-type(3) {
    width: 50%;
    float: right;
}

.e_navigationE .p_iconBox.showmenu span {
    background: #323538;
    -webkit-animation: animaspan 0.5s 1;
    animation: animaspan 0.5s 1;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
}

.e_navigationE .p_iconBox.showmenu span:nth-of-type(1) {
    transform: translateY(6px)rotate(720deg);
    width: 100%;
}

.e_navigationE .p_iconBox.showmenu span:nth-of-type(2) {
    transform: rotate(720deg);
}

.e_navigationE .p_iconBox.showmenu span:nth-of-type(3) {
    transform: translateY(-6px)rotate(720deg);
    width: 100%;
}

.e_navigationE .p_iconBox.showmenu span.biahuan {}

.e_navigationE .p_iconBox.showmenu span.biahuan:nth-of-type(1) {
    transform: translateY(6px)rotate(670deg);
}

.e_navigationE .p_iconBox.showmenu span.biahuan:nth-of-type(2) {
    transform: translateY(0px)rotate(768deg);
}

.e_navigationE .p_iconBox.showmenu span.biahuan:nth-of-type(3) {
    width: 0;
    opacity: 0;
}

.e_navigationE .p_navButton {
    width: 50px;
    display: flex;
    justify-content: center;
    padding: 10px 0;
}
.e_navigationE .p_navBox1{
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background: #f5f5f5;
    padding: 0 15px;
    height: calc(0vh);
    overflow: hidden;
    z-index: 10;
    transition: all ease .3s;
}
.e_navigationE .p_navBox1.pkcclb{
    height: calc(100vh);
}
.e_navigationE .p_navItem1 {
    border-bottom: 1px solid rgb(0 0 0 / 7%);
}
.e_navigationE .p_navItem1 > p{
    display: flex;
    justify-content: space-between;
}
.e_navigationE .p_navItem1 > p svg{
    width: auto;
    margin: 20px 0;
    opacity: .7;
    /* border-left: 1px solid rgb(0 0 0 / 10%); */
    padding-left: 10px;
    cursor: pointer;
    }
.e_navigationE .p_navItem1 a{
    line-height: 60px;
    font-size: 16px;
    color: rgb(0 0 0 / 70%);
    transition: all ease .3s;
}
.e_navigationE .p_navItem1 a:hover{
    color: rgb(0 0 0 / 100%);
}
.e_navigationE .p_navItem2 a{
    display: flex;
    justify-content: space-between;
    line-height: 30px;
    font-size: 14px;
    color: rgb(0 0 0 / 50%);
}
.e_navigationE .p_navItem2 .p_img,
.e_navigationE .p_level3Item span{
    /* order: -1; */
    width: 23px;
    margin-top: -2px;
    /* margin-right: 10px; */
    opacity: .8;
}
.e_navigationE .p_navItem2 .p_img img,
.e_navigationE .p_level3Item span img{
    width:100%;
}
.e_navigationE .p_navBox2{
    display: none;
}
.e_navigationE .p_navBox2 > div{
    padding-bottom: 20px;
}
.e_navigationE .p_menuItem{
    padding-left: 10px;
}
.e_navigationE .p_level3Box{
    margin: 5px 0 10px;
}

.e_navigationE .p_navBox1{
    display: flex;
    justify-content: flex-end;
}
.e_navigationE .p_navItem1  > p{
    line-height: 1;
    padding: 0 10px;
    display: flex;
}
.e_navigationE .p_navItem1 > p svg{
    display:none;
}
.e_navigationE .p_navItem1 > p a{
    font-size: 16px;
    line-height: 1;
    transition: all ease .3s;
    color: rgb(0 0 0 / 60%);
    position: relative;
    padding: 20px 0;
    display: block;
}
.e_navigationE .p_navItem1 > p a:hover{
    color: rgb(0 0 0 / 100%);
}
.e_navigationE .p_navItem1 > p a::after{
    content:'';
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    width: 0;
    height: 2px;
    background: #409eff;
    margin: 0 auto;
    transition: all ease .3s;
}
.e_navigationE .p_navItem1 > p a:hover::after{
    width: 100%;
}

.e_navigationE .p_navBox2{
    position: absolute;
    left: 0;
    right: 0;
    background: rgb(255 255 255 / 80%);
    top: 100%;
    display: flex;
    justify-content: flex-end;
    padding: 40px 0 40px;
    padding-right: 110px;
    z-index: 10;
    transition: all ease .3s;
    opacity: 0;
    visibility: hidden;
}
.e_navigationE .p_navItem1:hover .p_navBox2{
    opacity: 1;
    visibility: visible;
}
.e_navigationE .p_navBox2 ul{
    display: flex;
    justify-content: flex-end;
}
.e_navigationE .p_navItem2{
    margin-left: 60px;
}
.e_navigationE .p_navItem2 a{
    font-size: 19px;
    font-weight: bold;
    text-align: center;
    line-height: 1;
    color: rgb(0 0 0 / 80%);
    transition: all ease .3s;
}
.e_navigationE .p_navItem2 .p_img{
    width: 100%;
    display: block;
    max-width: 38px;
    margin: 20px auto 0;
}
.e_navigationE .p_navItem2 img{
    width: 100%;
}
.e_navigationE .haveThree .p_navItem2 .p_img{
    display: none;
}
.e_navigationE .haveThree .p_navItem2 a{
    text-align: left;
}
.e_navigationE .haveThree .p_level3Box{
    margin-top: 50px;
    margin-left: 3px;
}
.e_navigationE .haveThree .p_level3Item{
    margin-right: 15px;
}
.e_navigationE .haveThree .p_menuItem{
    font-size: 17px;
    font-weight: bold;
    border-left: 1px solid rgb(0 0 0 / 10%);
    padding-left: 15px;
}
.e_navigationE .haveThree .p_level3Item img{
    width: 35px;
    display: block;
    margin: 20px auto 0;
}
