css.cssText:
.e_navigationE{
    display: flex;
    justify-content: flex-end;
}
.e_navigationE .p_navButton{
    width: 100px;
    display: flex;
    justify-content: center;
    padding: 20px 0;
}
.e_navigationE .p_navButton > p{
    width: 21px;
    height: 16px;
    cursor: pointer;
}

.e_navigationE .p_navButton span {
    width: 100%;
    background: #333;
    display: block;
    height: 2px;
    margin: 2px 0;
    float: left;
    -webkit-transition: all ease 0.8s,opacity ease 0.3s;
    -moz-transition: all ease 0.8s,opacity ease 0.3s;
    -ms-transition: all ease 0.8s,opacity ease 0.3s;
    -o-transition: all ease 0.8s,opacity ease 0.3s;
    transition: all ease 0.8s,opacity ease 0.3s;
    opacity: 1;
}

.e_navigationE .p_navButton span:nth-of-type(1) {
    width: 50%;
    float: left;
}

.e_navigationE .p_navButton span:nth-of-type(2) {
}

.e_navigationE .p_navButton span:nth-of-type(3) {
    width: 50%;
    float: right;
}

.e_navigationE .p_navButton > p.showmenu span {
    background: #323538;
    -webkit-animation: animaspan 0.5s 1;
    animation: animaspan 0.5s 1;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
}

.e_navigationE .p_navButton > p.showmenu span:nth-of-type(1) {
    transform: translateY(6px)rotate(720deg);
    width: 100%;
}

.e_navigationE .p_navButton > p.showmenu span:nth-of-type(2) {
    transform: rotate(720deg);
}

.e_navigationE .p_navButton > p.showmenu span:nth-of-type(3) {
    transform: translateY(-6px)rotate(720deg);
    width: 100%;
}

.e_navigationE .p_navButton > p.showmenu span.biahuan:nth-of-type(1) {
    transform: translateY(6px)rotate(670deg);
}

.e_navigationE .p_navButton > p.showmenu span.biahuan:nth-of-type(2) {
    transform: translateY(0px)rotate(768deg);
}

.e_navigationE .p_navButton > p.showmenu span.biahuan:nth-of-type(3) {
    width: 0;
    opacity: 0;
}



.e_navigationE .p_navItem1 > p a{
    transition: all ease .3s;
    position: relative;
    padding: 20px 10px;
    display: block;
}
.e_navigationE .p_navCon{
    color: rgb(0 0 0 / 60%);
    font-size: 16px;
    line-height: 1;
}
.e_navigationE .p_navItem1 > p a:hover{
    color: rgb(0 0 0 / 100%);
}
.e_navigationE .p_navItem1 > p a::after{
    background: #409eff;
}
.e_navigationE .p_navItem2 a{
    font-size: 19px;
    font-weight: bold;
    text-align: center;
    line-height: 1;
    color: rgb(0 0 0 / 80%);
    transition: all ease .3s;
}
.e_navigationE .p_img{
    width: 100%;
    display: block;
    max-width: 35px;
    margin: 20px auto 0;
}
.e_navigationE .p_menuItem{
    font-size: 15px;
    border-left: 1px solid rgb(0 0 0 / 20%);
    padding-left: 15px;
    font-weight: 500;
    color: rgb(0 0 0 / 50%);
}
.e_navigationE .p_navItem2 img{
    width: 100%;
}
.e_navigationE .haveThree .p_navItem2 a{
    text-align: left;
}
.e_navigationE .p_level3Box{
    margin-top: 50px;
}
.e_navigationE .p_navBox2{
    background: rgb(255 255 255 / 80%);
    padding: 40px 110px 39px;
}

.havestatic > div,
.havestatic > div[class*="cbox-"] {
    position: static !important;
}

.e_navigationE .p_navBox1::-webkit-scrollbar {
    width: 3px;
}
.e_navigationE .p_navBox1::-webkit-scrollbar-track {
    background: #eee;
}
.e_navigationE .p_navBox1::-webkit-scrollbar-thumb {
    background: #d7d7d7;
}
.e_navigationE .p_navBox1::-webkit-scrollbar-thumb:hover {
    background: #fff;
}
.e_navigationE .p_navButton {
    width: 50px;
    display: flex;
    justify-content: center;
    padding: 10px 0;
}
.e_navigationE .p_navBox1{
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    background: #f5f5f5;
    padding: 0 15px;
    height: calc(0vh);
    overflow-y: scroll;
    z-index: 10;
    max-height: 1080px;
    transition: all ease .3s;
    width: 100%;
}
.e_navigationE .p_navBox1.pkcclb{
    height: calc(100vh);
}
.e_navigationE .p_navBox1.isdesign{
    height: calc(100vh);
    display: none;
}
.e_navigationE .p_navItem1 {
    border-bottom: 1px solid rgb(0 0 0 / 7%);
}
.e_navigationE .p_navCon{
    display: flex;
    justify-content: space-between;
    padding: 0;
    color: rgb(0 0 0 / 70%);
    font-size: 16px;
    line-height: 60px;
}
.e_navigationE .p_navItem1 > p svg{
    width: auto;
    margin: 18px 0;
    opacity: .7;
    cursor: pointer;
    transition: all ease .3s;
}
.e_navigationE .p_navItem1 > p svg.rotate{
    transform: rotate(90deg);
}
.e_navigationE .p_navItem1 > p a{
    padding: 0;
}
.e_navigationE .p_navItem1 a:hover{
    color: rgb(0 0 0 / 100%);
}
.e_navigationE .p_navItem2 a{
    display: flex;
    justify-content: space-between;
    line-height: 30px;
    font-size: 14px;
    color: rgb(0 0 0 / 50%);
    font-weight: 500;
}
.e_navigationE .p_navItem2 .p_img{
    width: 23px;
    opacity: .8;
    margin: 0;
}
.e_navigationE .p_navItem2 .p_img img,
.e_navigationE .p_level3Item span img{
    width:100%;
    margin: 0;
}
.e_navigationE .p_navBox2{
    display: none;
    padding: 0;
    background: rgb(255 255 255 / 0%);
}
.e_navigationE .p_navBox2 > div{
    padding-bottom: 20px;
}
.e_navigationE .p_menuItem{
    padding-left: 0;
    border: 0;
    font-size: 14px;
}
.e_navigationE .p_level3Box{
    margin: 10px 0;
    display: flex;
    flex-wrap: wrap;
    background: rgb(255 255 255 / 60%);
    padding: 15px;
}
.e_navigationE .p_level3Item span{
    display:none;
}
.e_navigationE .p_level3Item{
    width: 33.333%;
    display: inline-block;
}

.e_navigationE .p_navBox1{
    display: flex;
    justify-content: flex-end;
}
.e_navigationE .p_navItem1 > p{
    display: flex;
}
.e_navigationE .p_navItem1 > p svg{
    display:none;
}
.e_navigationE .p_navBox2{
    display: flex;
    justify-content: flex-end;
}
.e_navigationE .p_navBox2 ul{
    display: flex;
    justify-content: flex-end;
}
.e_navigationE .haveThree .p_navItem2 > a > .p_img{
    display: none;
}
.e_navigationE .haveThree .p_level3Item:nth-child(1) p{
    padding: 0;
    border: 0;
}
.e_navigationE .p_navItem2{
    margin-left: 50px;
}
.e_navigationE .p_navItem2:first-child{
    margin-left: 0px;
}
.e_navigationE .p_level3Item{
    margin-right: 15px;
}

.e_navigationE .p_navBox2{
    position: absolute;
    left: 0;
    right: 0;
    top: 56px;
    display: flex;
    justify-content: flex-end;
    z-index: 10;
    transition: all ease .3s;
    opacity: 0;
    visibility: hidden;
}
.e_navigationE .p_navItem1 > p a::after{
    content:'';
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    width: 0;
    height: 2px;
    margin: 0 auto;
    transition: all ease .3s;
}
.e_navigationE .p_navItem1 > p a:hover::after{
    width: 100%;
}
.e_navigationE .p_navItem1:hover .p_navBox2{
    opacity: 1;
    visibility: visible;
}
.e_navigationE .p_navBox2.isdesign{
    display: none;
    opacity: 1;
    visibility: visible;
}

.e_navigationE .p_navItem2{
    margin-left: 30px;
}
.e_navigationE .p_navBox2 .p_level3Box {
    flex-wrap: wrap;
    justify-content: flex-start;
    margin-top: 30px;
}
.e_navigationE .p_level3Item{
    margin-right: 0px;
    width: 100%;
    margin-bottom: 12px;
}
.e_navigationE .p_level3Item > a{
    display: flex;
}
.e_navigationE .p_level3Item img{
    margin: 0;
    width: 16px;
    margin-right: 5px;
}
.e_navigationE .haveThree .p_img{
    max-width: none;
    width: auto;
    margin: 0;
}
.e_navigationE .p_menuItem{
    border-left: 0px solid rgb(0 0 0 / 20%);
    padding: 0;
    order: 2;
}
.e_navigationE .haveThree .p_navItem2 {
    width: 25%;
}
.e_navigationE .p_img {
    display: flex;
    flex-direction: column;
    justify-content: center;
}
