css.cssText:
.e_navigationH{
    max-width: 100px;
    width: 100%;
    display: flex;
    justify-content: space-between;
    height: 100%;
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    border-right: 1px solid rgb(255 255 255 / 20%);
    transition: all ease .3s;
    z-index: 2;
}
.e_navigationH.on{
    border-right: 1px solid rgb(255 255 255 / 0%);
}
.e_navigationH .p_navButton{
    width: 100%;
    height: 100px;
    display: flex;
    flex-direction: column;
    justify-content: center;
    border-bottom: 1px solid rgb(255 255 255 / 20%);
    transition: all linear .3s;
    cursor: pointer;
    position: relative;
    z-index: 2;
}
.e_navigationH .p_navButton:hover,
.e_navigationH.on .p_navButton{
    background: #007bff;
    border-bottom: 1px solid rgb(0 123 255);
}
.e_navigationH .p_navButton > p{
    width: 21px;
    height: 16px;
    margin: 0 auto;
    background: none !important;
}
.e_navigationH .p_navButton > p span {
    width: 100%;
    background: #fff;
    display: block;
    height: 2px;
    margin: 2px 0;
    float: left;
    -webkit-transition: all ease 0.8s,opacity ease 0.3s;
    -moz-transition: all ease 0.8s,opacity ease 0.3s;
    -ms-transition: all ease 0.8s,opacity ease 0.3s;
    -o-transition: all ease 0.8s,opacity ease 0.3s;
    transition: all ease 0.8s,opacity ease 0.3s;
    opacity: 1;
}
.e_navigationH .p_navButton > p span:nth-of-type(1) {
    width: 50%;
    float: left;
}
.e_navigationH .p_navButton > p span:nth-of-type(3) {
    width: 50%;
    float: right;
}
.e_navigationH .p_navButton > p.showmenu span {
    -webkit-animation: animaspan 0.5s 1;
    animation: animaspan 0.5s 1;
    animation-fill-mode: forwards;
    -webkit-animation-fill-mode: forwards;
}
.e_navigationH .p_navButton > p.showmenu span:nth-of-type(1) {
    transform: translateY(6px)rotate(720deg);
    width: 100%;
}
.e_navigationH .p_navButton > p.showmenu span:nth-of-type(2) {
    transform: rotate(720deg);
}
.e_navigationH .p_navButton > p.showmenu span:nth-of-type(3) {
    transform: translateY(-6px)rotate(720deg);
    width: 100%;
}
.e_navigationH .p_navButton > p.showmenu span.biahuan:nth-of-type(1) {
    transform: translateY(6px)rotate(670deg);
}
.e_navigationH .p_navButton > p.showmenu span.biahuan:nth-of-type(2) {
    transform: translateY(0px)rotate(768deg);
}
.e_navigationH .p_navButton > p.showmenu span.biahuan:nth-of-type(3) {
    width: 0;
    opacity: 0;
}
.e_navigationH .p_bg{
    position: absolute;
    top: 100px;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgb(0 92 191);
    z-index: 1;
    transition: all ease .3s;
    pointer-events: none;
    width: 0;
    opacity: 0;
}
.e_navigationH .p_bg.showbg{
    pointer-events: auto;
    width: 100%;
    opacity: 1;
}
.e_navigationH .p_navContent {
    position: absolute;
    left: 100%;
    max-width: 340px;
    width: 340px;
    background: rgb(0 0 0 / 50%);
    transition: all ease .3s;
    top: 100px;
    bottom: 0;
    height: calc(100% - 100px);
    z-index: 2;
    opacity: 0;
    transform: perspective(300px) rotateY(90deg);
    transform-origin: left;
}
.e_navigationH .p_navContent.showmenuBox{
    opacity: 1;
    transform: perspective(300px) rotateY(0deg);
}
.e_navigationH .p_level1Box{
    padding-bottom: 60px;
}
.e_navigationH .p_level2Box{
    display: none;
    position: absolute;
    left: 100%;
    width: 70%;
    top: 0;
    background: rgb(0 0 0 / 80%);
    overflow-y: scroll;
}
.e_navigationH .p_level3Box{
    display: none;
}
.e_navigationH .p_menu1Item{
    display: flex;
    justify-content: space-between;
    line-height: 70px;
    font-size: 16px;
    color: #fff;
    transition: all ease .15s;
    border-bottom: 1px solid rgb(255 255 255 / 25%);
    padding: 0 15px;
}
.e_navigationH .p_menu2Item{
    display: flex;
    justify-content: space-between;
    line-height: 50px;
    font-size: 16px;
    color: #fff;
    transition: all ease .15s;
    padding: 0 15px;
}
.e_navigationH .p_menu3Item{
    display: flex;
    justify-content: space-between;
    line-height: 1;
    font-size: 15px;
    color: rgb(255 255 255 / .5);
    transition: all ease .15s;
    padding: 4px 15px;
}

.e_navigationH .p_ico1List{
    margin: 20px auto 0;
    width: 40px;
}
.e_navigationH .p_icoA{
    display: flex;
    flex-direction: row;
    border: 1px solid #fff;
    width: 100%;
    height: 40px;
    border-radius: 50px;
    justify-content: flex-start;
    transition: all ease .3s;
}
.e_navigationH .p_icoText{
    position: absolute;
    left: calc(100% + 5px);
    white-space: pre;
    color: #fff;
    display: flex;
    flex-direction: column;
    justify-content: center;
    height: 100%;
    transition: all ease .3s;
    opacity: 0;
    transform: translateX(10px);
}
.e_navigationH .p_icoImg{
    margin: 0;
}
.e_navigationH .p_icoImg img{
    position: absolute;
    left: 0;
    top: 50%;
    transform: translateY(-50%);
}
.e_navigationH .p_icoA:hover{
    background: #007bff;
    border: 1px solid #007bff;
    width: 130px;
}
.e_navigationH .p_icoA:hover .p_icoText{
    opacity: 1;
    transform: translateX(0);
}

.e_navigationH li{
    position:relative;
}
.e_navigationH a{
    display: flex;
    flex-direction: column;
    justify-content: center;
    width: 100%;
}
.e_navigationH svg{
    margin: 0;
    height: 18px;
    width: auto;
    cursor: pointer;
    transition: all ease .3s;
    transform: rotate(0deg)rotateY(0deg);
}
.e_navigationH p:hover{
    color: #fff;
    background: #005cbf;
}
.e_navigationH p:hover .p_jtIcon{
    transform: rotate(0deg)rotateX(180deg);
}
.e_navigationH .p_menu2Item:hover{
    color: #007bff;
    background: rgb(0 92 191 / 0);
}
.e_navigationH .p_menu3Item:hover{
    color: #007bff;
    background: rgb(0 92 191 / 0);
}
.e_navigationH .p_level2Box::-webkit-scrollbar {
    display: none;
}
.e_navigationH .p_icoBox{
    position: absolute;
    width: 100%;
    bottom: 40px;
    z-index: 2;
}

.e_navigationH{
    max-width: 55px;
}
.e_navigationH .p_navButton{
    height: 60px;
}
.e_navigationH .p_navContent{
    top: 60px;
    height: calc(100% - 60px);
    max-width: calc(100vw - 54px);
}
.e_navigationH .p_bg{
    top: 60px;
}
.e_navigationH .p_icoBox{
    bottom: 20px;
}
.e_navigationH .p_ico1List{
    margin: 10px auto 0;
}
.e_navigationH .p_menu1Item{
    line-height: 60px;
    padding: 0;
}
.e_navigationH .p_level2Box{
    position: static;
    width: 100%;
    background: rgb(0 0 0 / 40%);
}
.e_navigationH .p_navContent p > a{
    padding-left: 15px;
    width: calc(100% - 50px);
}
.e_navigationH .p_baoS{
    width: 50px;
    border-left: 1px solid rgb(255 255 255 / 25%);
    text-align: center;
    cursor: pointer;
}
.e_navigationH .p_menu2Item{
    padding: 0;
    border-bottom: 1px solid rgb(255 255 255 / 25%);
    color: rgb(255 255 255 / 70%);
}
.e_navigationH .p_menu3Item{
    padding: 0;
    line-height: 2;
}
.e_navigationH .p_level3Item:first-child .p_menu3Item{
    margin-top:10px;
}
.e_navigationH .p_level3Item:last-child .p_menu3Item{
    border-bottom: 1px solid rgb(255 255 255 / 25%);
    padding-bottom:10px;
}
