css.cssText:
.e_productScreen{
    overflow: hidden;
}
.e_productScreen .p_container{
    border-bottom: none;
}
.e_productScreen .p_categoryName{
    padding: 0 6px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}
.e_productScreen .p_valBox{
    max-width: 782px;
    display: flex;
    flex-wrap: wrap;
}
.e_productScreen .p_imgItem{
    float: left;
    width: 100px;
    height: 35px;
    padding: 2px;
    overflow: hidden;
    position: relative;
    margin-bottom: 7px;
    margin-right: -1px;
    text-align: center;
}
.e_productScreen .p_imgItem:hover{
    cursor: pointer;
    border: 1px solid #007BFF;
    z-index: 4;
}
.e_productScreen .p_checkIcon{
    display: none;
    width: 34px;
    height: 34px;
    position: absolute;
    bottom: -8px;
    right: -11px;
    z-index: 5;
    color: #007BFF;
} 
.e_productScreen img{
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.e_productScreen .p_valItem{
    margin-right: 36px;
    cursor: pointer;
}
.e_productScreen .p_qb{
    width: 40px;
    line-height: 30px;
    margin-right: 20px;
}
.e_productScreen .p_single{
    display: block;
    line-height: 30px;
}
.e_productScreen .p_RButton{
    width: 314px;
    /* text-align: right; */
    display: flex;
    align-items: flex-start;
    justify-content: right;
}
.e_productScreen label{
    margin: 0 2px;
}
.e_productScreen .p_checkedBox{
    display: none;
    width: 100%;
    margin-top: 5px;
}
.e_productScreen .p_BButton{
    display: none;
    width: 100%;
    margin-top: 5px;
    text-align: center;
}
.e_productScreen .p_checkedItem{
    display: inline-block;
    font-style: normal;
    padding: 3px 4px;
    margin-right: 6px;
    margin-bottom: 6px;
}
.e_productScreen .p_checkedItem i{
    font-style: normal;
}
.e_productScreen button{
    padding: 4px 6px;
    margin-left: 3px;
    outline: none;
}
.e_productScreen button:hover{
    color: #007BFF;
    border-color: #007BFF;
}
.e_productScreen button:hover .p_icon{
    color: #007BFF;
}
.e_productScreen .p_icon{
    color: #707070;
    margin-left: -3px;
    margin-right: -3px;
}
.e_productScreen .p_DelIcon{
    color: #8a8a8a;
}
.e_productScreen .p_DelIcon:hover{
    color: #007BFF;
    cursor: pointer;
}
.e_productScreen .p_hide{
    display: none;
}



.e_productScreen .p_checkbox,
.e_productScreen .foldBtn, 
.e_productScreen .moreBtn,
.e_productScreen .p_label{
    display: none;
}

body{
    padding: 0;
}
.e_productScreen{
    height: 100vh;
}
.e_productScreen .p_moSwitch{
    line-height: 30px;
    text-align: center;
}
.e_productScreen .p_moSwitch{
    /* display: none; */
    line-height: 28px;
    text-align: center;
}
.e_productScreen .p_iconUp{
    display: none;
}
.e_productScreen .p_container{
    width: 90%;
    height: 100vh;
    padding-bottom: 60px;
    overflow: auto;
    border-bottom-left-radius: 22px;
    border-top-left-radius: 22px;
    position: fixed;
    top: 0;
    right: -90%;
    border: none;
    transition: 0.5s;
}

.e_productScreen .p_container::-webkit-scrollbar {
    width: 2px;
}
.e_productScreen .p_container::-webkit-scrollbar-track {
    background-color: #f8f9fa;
    -webkit-border-radius: 2em;
    -moz-border-radius: 2em;
    border-radius: 2em;
}
.e_productScreen .p_container::-webkit-scrollbar-thumb {
    background-color: #dddfe0;
    -webkit-border-radius: 2em;
    -moz-border-radius: 2em;
    border-radius: 2em;
}
.e_productScreen .p_moWarp{
    margin-bottom: 12px;
    border-radius: 6px;
    overflow: hidden;
}
.e_productScreen .p_screenLine, .e_productScreen .p_brandLine{
    display: block;
    padding: 8px 10px 10px 10px;
}
.e_productScreen .p_brandLine{
    margin-bottom: 12px;
    border-radius: 6px;
}
.e_productScreen .p_lBox{
    width: 100%;
    display: flex;
    justify-content: space-between;
    border: none;
    color: black;
    margin-bottom: 8px;
    background: none;
}
.e_productScreen .p_moCheckBox{
    width: 68%;
    position: relative;
    text-align: right;
    line-height: 24px;
    font-size: 12px;
    padding-right: 22px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}
.e_productScreen .p_iconUp, .e_productScreen .p_iconDown{
    cursor: pointer;
    position: absolute;
    top: 5px;
    right: 0;
}
.e_productScreen .p_rBox{
    border: none;
    display: flex;
    flex-wrap: wrap;
}
.e_productScreen .p_screenLine .p_valBox{
    width: 100%;
    height: 0;
    overflow: hidden;
}
.e_productScreen .p_brandLine .p_valBox{
    max-height: 124px;
    overflow: hidden;
}
.e_productScreen .p_imgItem{
    width: 31%;
    margin-left: 2.3%;
    border: 1px solid #fff;
    overflow: hidden;
    margin-right: 0;
    border-radius: 20px;
}
.e_productScreen .p_valItem{
    float: left;
    display: block;
    min-width: 31%;
    max-width: 64%;
    border: 1px solid #fff;
    line-height: 35px;
    text-align: center;
    padding: 0 8px;
    margin-right: 2.3%;
    margin-bottom: 7px;
    border-radius: 20px;
    cursor: pointer;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
}
.e_productScreen .p_single{
    min-width: 78px;
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
}
.e_productScreen .p_moInput{
    width: 44.3%;
    height: 30px;
    text-align: center;
    padding: 0 10px;
    border-radius: 20px;
    outline: none;
    border: none;
}
.e_productScreen .p_moBBtn{
    width: 90%;
    height: 60px;
    display: flex;
    justify-content: space-around;
    align-items: center;
    position: fixed;
    right: -90%;
    bottom: 0;
    border-bottom-left-radius: 22px;
}
.e_productScreen .p_moResat,.e_productScreen .p_moConfirm{
    width: 42%;
    height: 30px;
    outline: none;
    border-radius: 20px;
    border: 1px solid #0b75ee;
}
.e_productScreen .p_moResat{
    background: rgba(64, 184, 240, 0.1);
}
.e_productScreen .setUpHeight{
    height: auto !important;
    max-height: 1000px !important;
}
.e_productScreen .checkItem{
    border: 1px solid #007BFF;
    background: #ecf5ff;
    color: #007BFF;
}

.e_productScreen .p_moContainer{
    transition: 0.5s;
    right: 0;
}
.e_productScreen .p_categoryBox{
    margin: 12px 0;
}

.e_productScreen .p_qb,
.e_productScreen .p_RButton,
.e_productScreen .p_checkedBox,
.e_productScreen .p_BButton,
.e_productScreen .p_bLine,
.e_productScreen .p_checkbox{
    display: none;
}

.e_productScreen .p_container{
    background: none;
    height: auto;
}
.e_productScreen .p_moCheckBox,
.e_productScreen .p_moSwitch,
.e_productScreen .p_iconUp,
.e_productScreen .p_iconDown,
.e_productScreen .p_moBBtn,
.e_productScreen .p_moInputBox{
    display: none;
}
.e_productScreen .p_screenLine, .e_productScreen .p_brandLine, .e_productScreen .p_checkedLine{
    display: flex;
}
.e_productScreen .p_lBox{
    width: 100px;
    min-height: 40px;
    display: flex;
    flex-shrink: 0;
    align-items: center;
    justify-content: center;
}
.e_productScreen .p_rBox{
    /* height: 50px; */
    flex-grow: 1;
    padding: 10px 20px;
    padding-right: 10px;
    min-height: 40px;
    display: flex;
    flex-wrap: wrap;
    overflow: hidden;
    justify-content: space-between;
}
.e_productScreen .p_bLine .p_rBox{
    padding-bottom: 4px;
}
.e_productScreen .p_screenLine .p_rBox{
    height: 51px;
}
.e_productScreen .p_imgBox{
    max-width: 714px;
    max-height: 126px;
    /* overflow-y: scroll; */
}
.e_productScreen .p_imgBox::-webkit-scrollbar {
    width: 3px;
  }
.e_productScreen .p_imgBox::-webkit-scrollbar-track {
    background-color: #dee2e6;
  }
.e_productScreen .p_imgBox::-webkit-scrollbar-thumb {
    background-color: #007bff;
  }
/* .e_productScreen .p_valBox{
    height: 30px;
    overflow: hidden;
} */
.e_productScreen .p_brandLine .p_rBox{
    height: 50px;
}
.e_productScreen .p_checkedLine:last-of-type .p_valBox{
    height: 31px;
}
.e_productScreen .p_valItem{
    background: none;
    line-height: 30px;
}
.e_productScreen .p_numInput{
    width: 55px;
    height: 27.72px;
    padding: 0 4px;
    outline: none;
}
/* .e_productScreen .p_dataInput{
    width:140px;
    height: 27.72px;
    outline: none;
} */
.e_productScreen .p_range{
    width:205px;
    height: 27.72px;
    padding-left: 10px;
}
.e_productScreen .p_start, .e_productScreen .p_end{
    width: 85px;
    height: 100%;
    text-align: center;
    border: none;
    outline: none;
}
.e_productScreen .p_start::placeholder, .e_productScreen .p_end::placeholder {
    text-align: center;
    font-size: 12px;
}

.e_register .p_fenge{
    display: block;
    width: 20px;
    text-align: center;
    float: left;
    color: #bbb;
}
.e_productScreen .p_setDown{
    height: auto !important;
}
.e_productScreen .showScroll{
    overflow-y: scroll;
}
