css.cssText:
.e_subForm .form-group{
    margin-bottom: 0;
}
.e_subForm .p_title{
    line-height: 40px;
}
.e_subForm .p_thead{
    background-color: #f0f0f0;
    height: 36px;
    line-height: 36px;
    display: flex;
}
.e_subForm .p_tbody{
    margin-bottom: 20px;
    min-height: 36px;
}
.e_subForm .p_tr{
    display: flex;
    flex-wrap: wrap;
    padding: 10px 0;
}
.e_subForm .p_th{
    flex: 1;
    padding: 0 5px;
}
.e_subForm .p_th:last-child{
    text-align: center;
}
.e_subForm .p_td{
    flex: 1;
    padding: 0 5px;
}
.e_subForm .p_td .p_label{
    display: none;
}
.e_subForm .p_td:last-child{
    display: flex;
    align-items: center;
    justify-content: center;
}
