css.cssText:
.e_turnPage {
    width: 100%;
    height: 800px;
    min-height: 300px;
    max-height: 100vh;
    overflow: hidden;
}
.e_turnPage .p_turnPage_container {
    width: 100%;
    height: 100%;
}
.e_turnPage .p_turnPage_slide:nth-child(odd) {
    background-color: #85d4e6;
}
.e_turnPage .p_turnPage_slide:nth-child(even) {
    background-color: #cfc7f4;
}
/* slide */
.e_turnPage .p_turnPage_slide {
    overflow-y: auto;
}
/* 分页器 */
.e_turnPage .p_turnPageItem {
    width: 25px;
    height: 25px;
    display: inline-block;
    margin: 10px;
    border-radius: 100%;
    border: 1px solid #d7d7d7;
}
/* 高亮 */
.e_turnPage .p_turnPageItem_active {
    background-color: #409eff;
    border-color: #409eff;
}
