css.cssText:
.e_video{
    width: 100%;
    position: relative;
}
.e_video video{
    width: 100%;
    display: block;
}
.e_video .p_video{
    position: relative;
}
.e_video .cover{
    width: 100%;
    height: 100%;
    position: absolute;
    left: 0;
    top: 0;
}
.e_video .cover .coverImage{
    width: 100%;
    height: 100%;
}
.e_video .cover .coverImage img{
    width: 100%;
    height: 100%;
    object-fit: contain;
}
.e_video .cover .playBtn{
    position:absolute;
    width: 80px;
    height: 80px;
    z-index: 2;
    left: 50%;
    top: 50%;
    margin: -40px auto auto -40px;
    cursor: pointer;
}
